import express from 'express';
import bodyParser from 'body-parser';
import cors from 'cors';
import morgan from 'morgan';
import dotenv from 'dotenv';
import routes from './routes';

dotenv.config();

const app = express();

app.get('/status', (req, res) => res.status(200)
  .json({ message: 'Welcome to search api' }));
app.enable('trust proxy');
// Enable Cross Origin Resource Sharing to all origins
app.use(cors());

// Log requests to the console.
const loggerFormat = '- [:date[web]] ":method :url" :status :response-time';

app.use(morgan(loggerFormat));

// Parse incoming requests data
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: false }));

// Define Route
app.use('/api/v1/', routes);

// error handlers
app.use('*', (req, res) => res.status(404).json({
  message: 'Well, will you help build this route? ðŸ¤·ðŸ¼â€â™‚ï¸',
}));

const hostname = process.env.hostname || '127.0.0.1';
const port = process.env.port || 9000;

app.listen(port, () => {
  // eslint-disable-next-line no-console
  console.log(`API is listening on ${hostname}:${port}`);
});

export default app;

import { Router } from 'express';
import Validator from '../middlewares/Validator';
import UsersController from '../controllers/UsersController';

const router = Router();

router.get('/users/:usernameString/:language', Validator.paramsChecker, UsersController.fetchUsers);

export default router;

/** Validates inputs class */
class Validator {
  /**
     * @static
     * @param {object} req
     * @param {object} res
     * @param {function} next
     * @memberof Validator
     * @returns {function} next
     */
  // eslint-disable-next-line consistent-return
  static paramsChecker(req, res, next) {
    const {
      usernameString,
      language,
    } = req.params;
    const { fallbacks } = req.query;
    if (usernameString.trim().length < 1 || language.trim().length < 1) {
      return res.status(400).json({
        message: 'invalid parameter,please provide a valid string',
        success: false,
      });
    }
    const checkParam = (parameters) => {
      const params = parameters.split(',');
      params.forEach((param) => {
        if (typeof param.trim() !== 'string' || param.trim().length < 1) {
          return res.status(400).json({
            message: 'invalid parameter, Please make sure all provided params are valid strings',
            success: false,
          });
        }
        return true;
      });
    };
    if (fallbacks) checkParam(fallbacks);
    return next();
  }
}

export default Validator;

import UserService from '../services/UserService';

/** Users Controller Class */
class UsersController {
  /**
   * @desc POST /api/v1/auth/verify
   * @param {object} req
   * @param {object} res
   * @memberof UsersController
   * @returns users
   */
  static async fetchUsers(req, res) {
    try {
      const { usernameString, language } = req.params;
      const { fallbacks } = req.query;
      const userService = new UserService();
      const result = await userService.fetchUsersByLanguage(usernameString, language, fallbacks);

      if (!result.totalCount) {
        return res.status(200).json({
          message: 'There are no users who match the search queries, please try including falsbacks',
          success: true,
          users: {},
        });
      }
      return res.status(200).json({
        message: 'Successfully retrieves users',
        success: true,
        result,
      });
    } catch (error) {
      return res.status(500).json({
        message: 'An unknown error occured,this could be a server error',
        success: false,
      });
    }
  }
}

export default UsersController;

/* eslint-disable no-await-in-loop */
import GitHubHelper from '../utils/GitHubHelper';

/**
   * Creates an instance of UserService.
   */
class UserService {
  /**
   * Creates an instance of UserService.
   * @param {object} param
   * @memberof UserService
   */
  constructor() {
    this.gitHubHelper = new GitHubHelper();
  }

  /**
   * Retrieves user details
   * @param {number} - id
   *@returns {object} - user
   */
  async fetchUsersByLanguage(username, language, fallbacks = '') {
    try {
      let users;
      let userDetails = {};
      users = await this.gitHubHelper.searchUsersByLanguage(username, language);

      users.fromFallBack = false;
      users.language = language;
      if (!users.total_count && fallbacks.length) {
        const fallBackArr = fallbacks.split(',');
        for (let index = 0; index < fallBackArr.length; index += 1) {
          users = await this.gitHubHelper
            .searchUsersByLanguage(username, fallBackArr[index]);
          if (users.total_count) {
            users.fromFallBack = true;
            users.language = fallBackArr[index];
            break;
          }
        }
      }
      const { language: programmingLanguage, fromFallBack, total_count: totalCount } = users;
      if (totalCount) {
        const data = await this.gitHubHelper.retrieveUserDetails(users.items);
        userDetails = {
          users: data,
          programmingLanguage,
          fromFallBack,
          totalCount,
        };
      }
      return userDetails;
    } catch (error) {
      throw error;
    }
  }
}
export default UserService;

import axios from 'axios';


/**
   * Creates an instance of GitHubHelper.
   */
class GitHubHelper {
  /**
   * Creates an instance of GitHubHelper.
   * @param {object} param
   * @memberof GitHubHelper
   */
  constructor() {
    this.request = axios.create({
      headers: {
        Authorization: `token ${process.env.GITHUB_OAUTH_TOKEN}`,
      },
    });
  }

  /**
   * Retrieves user details using languages as search query and user string
   * @param {number} - id
   *@returns {object} - user
   */
  async searchUsersByLanguage(username, language) {
    try {
      const user = await this.request.get(`${process.env.GITHUB_BASE_URL}/search/users?q=${username}+language:${language}`);
      return user.data;
    } catch (error) {
      throw error;
    }
  }

  /**
   * Retrieves user details using languages as search query and user string
   * @param {Array} - users
   *@returns {object} - user
   */
  async retrieveUserDetails(users) {
    const userURLs = users.map(user => user.url);

    const userDetails = await Promise
      .all(userURLs.map(async (userURL) => {
        const { data: userDetail } = await this.request.get(userURL);
        userDetail.username = userDetail.login;
        return userDetail;
      }));
    return userDetails;
  }
}

export default GitHubHelper;
